    import {useEffect,useState} from 'react';

function App() {
    const [crypto, setCrypto] = useState([])

    useEffect(() => {
        const loadData = () => {
            fetch('http://localhost:8000/api/crypto/')
                .then(response => response.json())
                .then(data => setCrypto(data))
        }
        loadData();
    },[])

  return (
    <div className="App">
      <header className="App-header">
          <h1>Dashboard</h1>
          {crypto.map(crypto => (
              <h1 key={crypto.id}>{crypto.nome} {crypto.sigla}</h1>
          ))}
      </header>
    </div>
  );
}

export default App;
