import React, {useState, useEffect} from 'react'
import { DataGrid } from '@mui/x-data-grid';

const columns = [
    {field: 'tipo', headerName: 'Operação'},
    {field: 'cryptomoeda', headerName: 'Moeda',      renderCell: (params) => {
       return <div className="rowitem">{params.row.family.name}</div>;
     },},
    {field: 'valor_operacao', headerName: 'Valor da operação'},
    {field: 'quantidade', headerName: 'Quantidade'},
    {field: 'exchange.nome', headerName: 'Exchange'},
    {field: 'data', headerName: 'Data da operação', width: '100%'}
]
const DataTable = () => {
    const [tableData, setTableData] = useState([])

    useEffect(() => {
        fetch("http://localhost:8000/api/finance/")
            .then((data) => data.json())
            .then((data) => setTableData(data))
    })

    return(
        <div style={{height: 700, width: '90%', textAlign: 'center'}}>
            <DataGrid
                rows={tableData}
                columns={columns}
                pageSize={10}
            />
        </div>
    )
}

export default DataTable
