import React, {useState, useEffect} from 'react'
import { DataGrid } from '@mui/x-data-grid';

const columns = [
    {field: 'tipo', headerName: 'Operação'},
    {field: 'cryptomoeda', headerName: 'Moeda',
      valueGetter: (params) => {
      let result = [];
      result.push(params.row.cryptomoeda[0].nome);
      return result;
    }},
    {field: 'valor_operacao', headerName: 'Valor da operação'},
    {field: 'quantidade', headerName: 'Quantidade'},
    {field: 'exchange.nome', headerName: 'Exchange', width: '100%',
      valueGetter: (params) => {
      let result = [];
      result.push(params.row.exchange[0].nome);
      return result;
    }},
    {field: 'data', headerName: 'Data da operação', width: '100%'}
]
const DataTable = () => {
    const [tableData, setTableData] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8000/api/finance/")
            .then((data) => data.json())
            .then((data) => setTableData(data))
    })

    return(
        <div style={{width: '90%', textAlign: 'center'}}>
            <h2>Negociações</h2>
            <div style={{ display: 'flex', height: '100%' }}>
                <DataGrid autoHeight
                    rows={tableData}
                    columns={columns}
                    pageSize={20}
                    showColumnRightBorder
                />
            </div>
        </div>
    )
}

export default DataTable
